create table member
(
    member_code    int                         not null
        primary key,
    name       varchar(50) not null,
    points       int default 0               not null,
    last_use_date date                        null
);


create table product
(
    product_code     int          not null
        primary key,
    product_category_code int          not null,
    product_name      varchar(100) not null,
    unit_price        int          not null,
    last_arrival_date  date         null,
    model_number        varchar(10)  null,
    discounted_price    int          null
);


create table product_category
(
    product_category_code int                          not null
        primary key,
    product_category_name  varchar(100) not null
);



CREATE TABLE sales
(
    sales_number      INT NOT NULL PRIMARY KEY,
    sales_date_time   TIMESTAMP NOT NULL,
    member_code       INT NOT NULL,
    redeemed_points   INT DEFAULT 0 NOT NULL
);
# 日付の正規表現 (\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2})
# 置き換え文字 TO_TIMESTAMP('\1', 'YYYY-MM-DD HH24:MI:SS')



create table sales_details
(
    sales_number int           not null,
    detail_number int           not null,
    product_code  int           not null,
    amount     int default 0 not null,
    quantity   int default 1 not null,
    primary key (sales_number, detail_number)
);

